custom_css:
/* Arcane Drift signature weave */
body {
    color: rgba(172, 236, 219, 1);
    background-color: rgba(35, 8, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(340deg, transparent 0 40px, rgba(194, 206, 188, 1.00) 40px 80px),
        radial-gradient(circle at 68% 65%, rgba(178, 156, 226, 1) 0%, transparent 55%),
        linear-gradient(216deg, rgba(133, 168, 214, 1), rgba(178, 156, 226, 1));
    animation: arcane-drift-bg 11.58s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 221deg, transparent 0 25%, rgba(178, 156, 226, 1) 45%, transparent 75%);
    animation: arcane-drift-mesh 19.41s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: arcane-drift-scan 12.22s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 29, 6, 0.68), rgba(7, 54, 115, 0.7));
    border: 1px solid rgba(61, 16, 158, 0.88);
    box-shadow: 0 0 34px rgba(178, 156, 226, 1);
    backdrop-filter: blur(6.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-2.58deg) rotateY(-2.92deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(178, 156, 226, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(178, 156, 226, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(178, 156, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(178, 156, 226, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(178, 156, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(178, 156, 226, 1));
}

::selection {
    background: rgba(178, 156, 226, 1);
    color: rgba(172, 236, 219, 1);
}

@keyframes arcane-drift-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.56%, -2.54%, 0); }
    100% { transform: scale(1.12) translate3d(2.65%, 0.31%, 0); }
}

@keyframes arcane-drift-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes arcane-drift-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes arcane-drift-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
