custom_css:
/* Arcane Nebula signature weave */
body {
    color: rgba(172, 220, 236, 1);
    background-color: rgba(80, 8, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 4% 0%, rgba(139, 133, 214, 1) 0%, transparent 58%),
        radial-gradient(circle at 52% 15%, rgba(215, 156, 226, 1) 0%, transparent 64%),
        conic-gradient(from 316deg, rgba(172, 220, 236, 1), rgba(226, 205, 141, 1), rgba(215, 156, 226, 1), rgba(139, 133, 214, 1), rgba(172, 220, 236, 1));
    animation: arcane-nebula-bg 11.22s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(50deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(194, 214, 198, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: arcane-nebula-orb 10.55s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(115deg, transparent 0 28%, rgba(185, 147, 221, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: arcane-nebula-scan 11.50s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 81, 6, 0.68), rgba(16, 7, 115, 0.7));
    border: 1px solid rgba(134, 16, 158, 0.88);
    box-shadow: 0 0 34px rgba(215, 156, 226, 1);
    backdrop-filter: blur(8.0px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-2.43deg) rotateY(2.79deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(139, 133, 214, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(139, 133, 214, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(215, 156, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(139, 133, 214, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(215, 156, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(215, 156, 226, 1));
}

::selection {
    background: rgba(139, 133, 214, 1);
    color: rgba(172, 220, 236, 1);
}

@keyframes arcane-nebula-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-0.30%, -0.56%, 0); }
    100% { transform: scale(1.12) translate3d(2.82%, -2.37%, 0); }
}

@keyframes arcane-nebula-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes arcane-nebula-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes arcane-nebula-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
