custom_css:
/* Auric Flux signature weave */
body {
    color: rgba(236, 172, 234, 1);
    background-color: rgba(94, 61, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(235deg, transparent 0 40px, rgba(198, 194, 215, 1.00) 40px 80px),
        radial-gradient(circle at 49% 15%, rgba(236, 172, 234, 1) 0%, transparent 55%),
        linear-gradient(208deg, rgba(214, 133, 143, 1), rgba(226, 199, 156, 1));
    animation: auric-flux-bg 18.82s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 98deg, transparent 0 25%, rgba(141, 226, 186, 1) 45%, transparent 75%);
    animation: auric-flux-mesh 24.35s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: auric-flux-scan 18.64s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 107, 59, 0.68), rgba(115, 7, 22, 0.7));
    border: 1px solid rgba(158, 103, 16, 0.88);
    box-shadow: 0 0 34px rgba(141, 226, 186, 1);
    backdrop-filter: blur(8.2px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.53deg) rotateY(2.18deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(236, 172, 234, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(236, 172, 234, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(141, 226, 186, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(236, 172, 234, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(141, 226, 186, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(141, 226, 186, 1));
}

::selection {
    background: rgba(236, 172, 234, 1);
    color: rgba(236, 172, 234, 1);
}

@keyframes auric-flux-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-0.91%, -2.81%, 0); }
    100% { transform: scale(1.12) translate3d(3.43%, 1.67%, 0); }
}

@keyframes auric-flux-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes auric-flux-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes auric-flux-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
