custom_css:
body {
  background: linear-gradient(160deg, #07090f 0%, #0d1420 45%, #11253a 100%);
  color: #f4fffa;
}
main, .tray {
  background: rgba(12, 17, 26, 0.72);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(132, 214, 255, 0.18);
}
.mes, .mes_text {
  background: rgba(18, 26, 40, 0.78);
  border: 1px solid rgba(255, 186, 120, 0.35);
  box-shadow: 0 16px 36px rgba(4, 6, 12, 0.6);
  position: relative;
  overflow: hidden;
}
.mes::before {
  content: "";
  position: absolute;
  inset: -2px;
  border: 1px solid rgba(169, 255, 214, 0.35);
  border-radius: inherit;
  pointer-events: none;
  mix-blend-mode: screen;
}
.mes::after {
  content: "";
  position: absolute;
  inset: 30% -40% -40% 30%;
  background: radial-gradient(circle, rgba(255, 186, 120, 0.3) 0%, transparent 70%);
  transform: rotate(18deg);
  filter: blur(12px);
  opacity: 0.8;
}
button {
  background: linear-gradient(135deg, rgba(255, 186, 120, 0.25), rgba(132, 214, 255, 0.3));
  border: 1px solid rgba(169, 255, 214, 0.4);
  color: #f4fffa;
}
button:hover {
  filter: brightness(1.1);
}
#send_form textarea {
  background: rgba(10, 16, 26, 0.85);
  border: 1px solid rgba(132, 214, 255, 0.35);
  color: #f4fffa;
}
::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, #ff9f43, #3cdfff);
}
