custom_css:
/* Azure Overdrive signature weave */
body {
    color: rgba(174, 172, 236, 1);
    background-color: rgba(94, 8, 41, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 67% 75%, rgba(181, 226, 141, 1) 0%, transparent 58%),
        radial-gradient(circle at 59% 7%, rgba(226, 156, 183, 1) 0%, transparent 64%),
        conic-gradient(from 274deg, rgba(174, 172, 236, 1), rgba(181, 226, 141, 1), rgba(226, 156, 183, 1), rgba(203, 133, 214, 1), rgba(174, 172, 236, 1));
    animation: azure-overdrive-bg 10.19s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-51deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(177, 194, 198, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: azure-overdrive-orb 24.18s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(302deg, transparent 0 28%, rgba(217, 147, 195, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: azure-overdrive-scan 9.85s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(53, 107, 6, 0.68), rgba(101, 7, 115, 0.7));
    border: 1px solid rgba(158, 16, 70, 0.88);
    box-shadow: 0 0 34px rgba(226, 156, 183, 1);
    backdrop-filter: blur(11.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.43deg) rotateY(-1.86deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(181, 226, 141, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(181, 226, 141, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(226, 156, 183, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(181, 226, 141, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(226, 156, 183, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(226, 156, 183, 1));
}

::selection {
    background: rgba(181, 226, 141, 1);
    color: rgba(174, 172, 236, 1);
}

@keyframes azure-overdrive-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(1.74%, 2.60%, 0); }
    100% { transform: scale(1.12) translate3d(-1.59%, 4.03%, 0); }
}

@keyframes azure-overdrive-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes azure-overdrive-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes azure-overdrive-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
