custom_css:
/* Celestial Reverie signature weave */
body {
    color: rgba(236, 172, 201, 1);
    background-color: rgba(83, 94, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(159deg, rgba(214, 164, 133, 1) 0%, transparent 48%),
        conic-gradient(from 214deg at 50% 50%, rgba(214, 164, 133, 1), rgba(217, 226, 156, 1), rgba(141, 222, 226, 1), rgba(214, 164, 133, 1));
    animation: celestial-reverie-bg 14.59s ease-in-out infinite;
    filter: hue-rotate(-154deg) saturate(160%);
}

body::before {
    background: radial-gradient(circle at 75% 20%, rgba(198, 192, 211, 1.00) 0%, transparent 60%);
    clip-path: polygon(0% 12%, 35% 0%, 65% 8%, 100% 0%, 100% 88%, 68% 100%, 32% 92%, 0% 100%);
    animation: celestial-reverie-mesh 25.20s ease-in-out infinite alternate;
    mix-blend-mode: hard-light;
    opacity: 0.38;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 45%, rgba(0, 0, 0, 0.55) 70%);
    mix-blend-mode: multiply;
    animation: celestial-reverie-scan 17.46s linear infinite;
    opacity: 0.4;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 102, 107, 0.68), rgba(115, 49, 7, 0.7));
    border: 1px solid rgba(139, 158, 16, 0.88);
    box-shadow: 0 0 34px rgba(217, 226, 156, 1);
    backdrop-filter: blur(10.2px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(1.71deg) rotateY(2.31deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(214, 164, 133, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(214, 164, 133, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(217, 226, 156, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(214, 164, 133, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(217, 226, 156, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(217, 226, 156, 1));
}

::selection {
    background: rgba(214, 164, 133, 1);
    color: rgba(236, 172, 201, 1);
}

@keyframes celestial-reverie-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(2.63%, 0.69%, 0); }
    100% { transform: scale(1.12) translate3d(-3.28%, -1.75%, 0); }
}

@keyframes celestial-reverie-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes celestial-reverie-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes celestial-reverie-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
