custom_css:
/* Chromatic Eclipse signature weave */
body {
    color: rgba(218, 236, 172, 1);
    background-color: rgba(8, 94, 83, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 26% 57%, rgba(208, 141, 226, 1) 0%, transparent 58%),
        radial-gradient(circle at 47% 60%, rgba(156, 226, 217, 1) 0%, transparent 64%),
        conic-gradient(from 163deg, rgba(218, 236, 172, 1), rgba(208, 141, 226, 1), rgba(156, 226, 217, 1), rgba(133, 214, 142, 1), rgba(218, 236, 172, 1));
    animation: chromatic-eclipse-bg 22.71s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(17deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(214, 198, 194, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: chromatic-eclipse-orb 27.61s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(105deg, transparent 0 28%, rgba(147, 221, 187, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: chromatic-eclipse-scan 13.89s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(85, 6, 107, 0.68), rgba(7, 115, 20, 0.7));
    border: 1px solid rgba(16, 158, 139, 0.88);
    box-shadow: 0 0 34px rgba(156, 226, 217, 1);
    backdrop-filter: blur(6.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-2.42deg) rotateY(1.43deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(208, 141, 226, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(208, 141, 226, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(156, 226, 217, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(208, 141, 226, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(156, 226, 217, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(156, 226, 217, 1));
}

::selection {
    background: rgba(208, 141, 226, 1);
    color: rgba(218, 236, 172, 1);
}

@keyframes chromatic-eclipse-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(2.55%, 1.64%, 0); }
    100% { transform: scale(1.12) translate3d(-0.36%, -0.04%, 0); }
}

@keyframes chromatic-eclipse-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes chromatic-eclipse-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes chromatic-eclipse-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
