custom_css:
body {
    background-color: rgba(4, 68, 40, 1);
    background-image:
        radial-gradient(circle at 18% 22%, rgba(29, 9, 144, 0.55) 0%, transparent 58%),
        radial-gradient(circle at 82% 28%, rgba(136, 16, 7, 0.5) 0%, transparent 62%),
        conic-gradient(from 0deg, rgba(6, 116, 69, 0.5), rgba(25, 7, 126, 0.55), rgba(107, 12, 6, 0.5), rgba(6, 116, 69, 0.5));
    background-size: cover;
    background-attachment: fixed;
    animation: chromatic-matrix-background 24s ease-in-out infinite alternate;
    color: rgba(172, 236, 208, 1);
}

body::before {
    content: '';
    position: fixed;
    inset: -12vh;
    background: radial-gradient(circle, rgba(102, 80, 226, 0.72) 0%, transparent 62%);
    opacity: 0.28;
    mix-blend-mode: screen;
    filter: blur(42px);
    animation: chromatic-matrix-aurora 16s ease-in-out infinite alternate;
    pointer-events: none;
}

body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
    background: linear-gradient(125deg, transparent 0 18%, rgba(62, 147, 204, 0.35) 38%, transparent 68%, rgba(79, 199, 35, 0.32) 92%);
    mix-blend-mode: overlay;
    opacity: 0.15;
    animation: chromatic-matrix-scan 20s linear infinite;
}

.mes, .mesText, .swipe-message {
    background-color: rgba(8, 12, 28, 0.35);
    background-image: linear-gradient(135deg, rgba(11, 122, 74, 0.6) 0%, rgba(28, 8, 145, 0.62) 100%);
    border: 1px solid rgba(37, 16, 158, 0.88);
    box-shadow: 0 0 26px rgba(40, 9, 215, 0.62);
    backdrop-filter: blur(10px) saturate(185%);
    transition: transform 0.55s ease, box-shadow 0.55s ease;
}

.mes:hover {
    transform: translateY(-6px) scale(1.015);
    box-shadow: 0 0 42px rgba(247, 24, 8, 0.76);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.52);
    color: rgba(94, 66, 250, 0.7);
    box-shadow: 0 0 12px rgba(102, 80, 226, 0.72);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(102, 80, 226, 0.72);
    background: rgba(2, 8, 20, 0.32);
    box-shadow: inset 0 0 18px rgba(40, 9, 215, 0.62);
}

::selection {
    background: rgba(94, 66, 250, 0.7);
    color: rgba(225, 181, 152, 1);
}

@keyframes chromatic-matrix-background {
    0% { filter: hue-rotate(0deg) saturate(1); }
    50% { filter: hue-rotate(95deg) saturate(1.85); }
    100% { filter: hue-rotate(360deg) saturate(1); }
}

@keyframes chromatic-matrix-aurora {
    0% { transform: translate(-6%, -6%) scale(1); opacity: 0.22; }
    50% { transform: translate(6%, 6%) scale(1.12); opacity: 0.36; }
    100% { transform: translate(-8%, 4%) scale(1.04); opacity: 0.26; }
}

@keyframes chromatic-matrix-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.22; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}
