custom_css:
:root {
  --theme-glow: 0 0 24px rgba(255, 71, 205, 0.7);
  --theme-gradient: linear-gradient(140deg, #260051 0%, #ff2975 40%, #05d9e8 100%);
}
body {
  background: radial-gradient(circle at 10% 10%, rgba(5, 217, 232, 0.2), transparent 55%),
              radial-gradient(circle at 90% 20%, rgba(255, 41, 117, 0.25), transparent 60%),
              #0a0516;
  color: #ffe3ff;
}
.mes, .mes_text {
  background-image: var(--theme-gradient);
  background-size: 200% 200%;
  animation: pulseSlide 16s ease infinite;
  border: 1px solid rgba(5, 217, 232, 0.3);
  box-shadow: var(--theme-glow);
}
#send_form textarea {
  background: rgba(18, 8, 40, 0.88);
  border: 1px solid rgba(255, 71, 205, 0.45);
  box-shadow: 0 0 18px rgba(5, 217, 232, 0.25);
}
@keyframes pulseSlide {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}
