custom_css:
body {
    background-color: rgba(9, 4, 68, 1);
    background-image:
        radial-gradient(circle at 18% 22%, rgba(144, 9, 54, 0.55) 0%, transparent 58%),
        radial-gradient(circle at 82% 28%, rgba(61, 136, 7, 0.5) 0%, transparent 62%),
        conic-gradient(from 0deg, rgba(15, 6, 116, 0.5), rgba(126, 7, 46, 0.55), rgba(48, 107, 6, 0.5), rgba(15, 6, 116, 0.5));
    background-size: cover;
    background-attachment: fixed;
    animation: dreamwave-cascade-background 24s ease-in-out infinite alternate;
    color: rgba(178, 172, 236, 1);
}

body::before {
    content: '';
    position: fixed;
    inset: -12vh;
    background: radial-gradient(circle, rgba(226, 80, 129, 0.72) 0%, transparent 62%);
    opacity: 0.28;
    mix-blend-mode: screen;
    filter: blur(42px);
    animation: dreamwave-cascade-aurora 16s ease-in-out infinite alternate;
    pointer-events: none;
}

body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
    background: linear-gradient(125deg, transparent 0 18%, rgba(192, 62, 204, 0.35) 38%, transparent 68%, rgba(35, 158, 199, 0.32) 92%);
    mix-blend-mode: overlay;
    opacity: 0.15;
    animation: dreamwave-cascade-scan 20s linear infinite;
}

.mes, .mesText, .swipe-message {
    background-color: rgba(8, 12, 28, 0.35);
    background-image: linear-gradient(135deg, rgba(20, 11, 122, 0.6) 0%, rgba(145, 8, 54, 0.62) 100%);
    border: 1px solid rgba(158, 16, 63, 0.88);
    box-shadow: 0 0 26px rgba(215, 9, 78, 0.62);
    backdrop-filter: blur(11px) saturate(185%);
    transition: transform 0.55s ease, box-shadow 0.55s ease;
}

.mes:hover {
    transform: translateY(-6px) scale(1.015);
    box-shadow: 0 0 42px rgba(108, 247, 8, 0.76);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.52);
    color: rgba(250, 66, 127, 0.7);
    box-shadow: 0 0 12px rgba(226, 80, 129, 0.72);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 80, 129, 0.72);
    background: rgba(2, 8, 20, 0.32);
    box-shadow: inset 0 0 18px rgba(215, 9, 78, 0.62);
}

::selection {
    background: rgba(250, 66, 127, 0.7);
    color: rgba(158, 225, 152, 1);
}

@keyframes dreamwave-cascade-background {
    0% { filter: hue-rotate(0deg) saturate(1); }
    50% { filter: hue-rotate(95deg) saturate(1.85); }
    100% { filter: hue-rotate(360deg) saturate(1); }
}

@keyframes dreamwave-cascade-aurora {
    0% { transform: translate(-6%, -6%) scale(1); opacity: 0.22; }
    50% { transform: translate(6%, 6%) scale(1.12); opacity: 0.36; }
    100% { transform: translate(-8%, 4%) scale(1.04); opacity: 0.26; }
}

@keyframes dreamwave-cascade-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.22; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}
