custom_css:
/* Electric Horizon signature weave */
body {
    color: rgba(236, 176, 172, 1);
    background-color: rgba(39, 94, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(202deg, transparent 0 40px, rgba(198, 177, 194, 1.00) 40px 80px),
        radial-gradient(circle at 6% 60%, rgba(214, 205, 133, 1) 0%, transparent 55%),
        linear-gradient(325deg, rgba(214, 205, 133, 1), rgba(182, 226, 156, 1));
    animation: electric-horizon-bg 19.25s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 294deg, transparent 0 25%, rgba(214, 205, 133, 1) 45%, transparent 75%);
    animation: electric-horizon-mesh 15.89s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: electric-horizon-scan 16.05s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 51, 107, 0.68), rgba(115, 102, 7, 0.7));
    border: 1px solid rgba(68, 158, 16, 0.88);
    box-shadow: 0 0 34px rgba(214, 205, 133, 1);
    backdrop-filter: blur(11.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(0.16deg) rotateY(-3.00deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(214, 205, 133, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(214, 205, 133, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(214, 205, 133, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(214, 205, 133, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(214, 205, 133, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(214, 205, 133, 1));
}

::selection {
    background: rgba(214, 205, 133, 1);
    color: rgba(236, 176, 172, 1);
}

@keyframes electric-horizon-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.32%, -2.77%, 0); }
    100% { transform: scale(1.12) translate3d(3.34%, -1.53%, 0); }
}

@keyframes electric-horizon-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes electric-horizon-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes electric-horizon-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
