custom_css:
/* Galactic Prism signature weave */
body {
    color: rgba(172, 188, 236, 1);
    background-color: rgba(94, 8, 65, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(303deg, rgba(226, 156, 203, 1), transparent 45%),
        repeating-radial-gradient(circle, transparent 0 12px, rgba(185, 203, 198, 1.00) 12px 18px),
        linear-gradient(15deg, rgba(226, 156, 203, 1), rgba(180, 133, 214, 1), rgba(205, 226, 141, 1));
    animation: galactic-prism-bg 23.22s alternate infinite;
    background-size: cover, 280px 280px, cover;
    filter: saturate(170%) brightness(95%);
}

body::before {
    background: repeating-linear-gradient(23deg, transparent 0 22px, rgba(208, 147, 207, 1.00) 22px 44px);
    mask-image: radial-gradient(circle at center, rgba(0, 0, 0, 1) 0%, transparent 68%);
    animation: galactic-prism-mesh 17.14s alternate infinite;
    opacity: 0.3;
}

body::after {
    background: radial-gradient(circle at 75% 37%, rgba(205, 226, 141, 1) 0%, transparent 68%);
    mix-blend-mode: screen;
    animation: galactic-prism-orb 9.29s ease-in-out infinite;
    opacity: 0.28;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(81, 107, 6, 0.68), rgba(70, 7, 115, 0.7));
    border: 1px solid rgba(158, 16, 110, 0.88);
    box-shadow: 0 0 34px rgba(205, 226, 141, 1);
    backdrop-filter: blur(10.1px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.68deg) rotateY(1.08deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 156, 203, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 156, 203, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(205, 226, 141, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 156, 203, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(205, 226, 141, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(205, 226, 141, 1));
}

::selection {
    background: rgba(226, 156, 203, 1);
    color: rgba(172, 188, 236, 1);
}

@keyframes galactic-prism-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(1.09%, -2.66%, 0); }
    100% { transform: scale(1.12) translate3d(1.76%, 4.80%, 0); }
}

@keyframes galactic-prism-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes galactic-prism-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes galactic-prism-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
