custom_css:
/* Glimmer Rift signature weave */
body {
    color: rgba(236, 172, 229, 1);
    background-color: rgba(94, 67, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        conic-gradient(from 325deg, rgba(236, 172, 229, 1), rgba(226, 204, 156, 1), rgba(141, 226, 192, 1), rgba(214, 133, 138, 1), rgba(236, 172, 229, 1)),
        radial-gradient(circle at 65% 21%, rgba(226, 204, 156, 1) 0%, transparent 70%);
    animation: glimmer-rift-bg 9.57s ease-in-out infinite;
    filter: saturate(150%) brightness(105%);
}

body::before {
    background: repeating-conic-gradient(from 264deg, transparent 0 15deg, rgba(198, 194, 214, 1.00) 15deg 30deg);
    animation: glimmer-rift-mesh 14.47s linear infinite;
    mix-blend-mode: color-dodge;
    opacity: 0.24;
}

body::after {
    background: radial-gradient(circle at center, transparent 0 35%, rgba(0, 0, 0, 0.55) 60%);
    backdrop-filter: blur(8px) saturate(190%);
    animation: glimmer-rift-scan 8.12s ease-in-out infinite;
    opacity: 0.36;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 107, 66, 0.68), rgba(115, 7, 15, 0.7));
    border: 1px solid rgba(158, 113, 16, 0.88);
    box-shadow: 0 0 34px rgba(226, 204, 156, 1);
    backdrop-filter: blur(4.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(2.47deg) rotateY(2.05deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 204, 156, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 204, 156, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(226, 204, 156, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 204, 156, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(226, 204, 156, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(226, 204, 156, 1));
}

::selection {
    background: rgba(226, 204, 156, 1);
    color: rgba(236, 172, 229, 1);
}

@keyframes glimmer-rift-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.56%, 0.31%, 0); }
    100% { transform: scale(1.12) translate3d(-2.41%, 3.11%, 0); }
}

@keyframes glimmer-rift-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes glimmer-rift-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes glimmer-rift-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
