custom_css:
/* Kaleido Overdrive signature weave */
body {
    color: rgba(172, 206, 236, 1);
    background-color: rgba(94, 8, 90, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 31% 6%, rgba(226, 156, 223, 1) 0%, transparent 58%),
        radial-gradient(circle at 0% 64%, rgba(172, 206, 236, 1) 0%, transparent 64%),
        conic-gradient(from 171deg, rgba(172, 206, 236, 1), rgba(226, 224, 141, 1), rgba(226, 156, 223, 1), rgba(157, 133, 214, 1), rgba(172, 206, 236, 1));
    animation: kaleido-overdrive-bg 14.77s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(21deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(194, 213, 198, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: kaleido-overdrive-orb 25.66s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(174deg, transparent 0 28%, rgba(198, 147, 219, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: kaleido-overdrive-scan 10.90s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 103, 6, 0.68), rgba(40, 7, 115, 0.7));
    border: 1px solid rgba(158, 16, 151, 0.88);
    box-shadow: 0 0 34px rgba(172, 206, 236, 1);
    backdrop-filter: blur(11.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.89deg) rotateY(-2.68deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 156, 223, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 156, 223, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(172, 206, 236, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 156, 223, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(172, 206, 236, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(172, 206, 236, 1));
}

::selection {
    background: rgba(226, 156, 223, 1);
    color: rgba(172, 206, 236, 1);
}

@keyframes kaleido-overdrive-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-0.70%, -2.89%, 0); }
    100% { transform: scale(1.12) translate3d(-1.08%, 0.90%, 0); }
}

@keyframes kaleido-overdrive-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes kaleido-overdrive-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes kaleido-overdrive-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
