custom_css:
/* Kaleido Prism signature weave */
body {
    color: rgba(172, 217, 236, 1);
    background-color: rgba(84, 8, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(222deg, transparent 0 40px, rgba(194, 214, 198, 1.00) 40px 80px),
        radial-gradient(circle at 15% 14%, rgba(143, 133, 214, 1) 0%, transparent 55%),
        linear-gradient(203deg, rgba(143, 133, 214, 1), rgba(218, 156, 226, 1));
    animation: kaleido-prism-bg 19.85s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 271deg, transparent 0 25%, rgba(218, 156, 226, 1) 45%, transparent 75%);
    animation: kaleido-prism-mesh 12.88s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: kaleido-prism-scan 7.00s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 86, 6, 0.68), rgba(22, 7, 115, 0.7));
    border: 1px solid rgba(141, 16, 158, 0.88);
    box-shadow: 0 0 34px rgba(218, 156, 226, 1);
    backdrop-filter: blur(4.1px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.44deg) rotateY(-0.92deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(143, 133, 214, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(143, 133, 214, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(218, 156, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(143, 133, 214, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(218, 156, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(218, 156, 226, 1));
}

::selection {
    background: rgba(143, 133, 214, 1);
    color: rgba(172, 217, 236, 1);
}

@keyframes kaleido-prism-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-1.10%, 0.14%, 0); }
    100% { transform: scale(1.12) translate3d(-1.81%, -1.73%, 0); }
}

@keyframes kaleido-prism-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes kaleido-prism-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes kaleido-prism-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
