custom_css:
/* Luminous Galaxy signature weave */
body {
    color: rgba(182, 236, 172, 1);
    background-color: rgba(8, 57, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 60% 61%, rgba(226, 141, 196, 1) 0%, transparent 58%),
        radial-gradient(circle at 43% 0%, rgba(226, 141, 196, 1) 0%, transparent 64%),
        conic-gradient(from 53deg, rgba(182, 236, 172, 1), rgba(226, 141, 196, 1), rgba(156, 196, 226, 1), rgba(133, 214, 188, 1), rgba(182, 236, 172, 1));
    animation: luminous-galaxy-bg 15.75s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-119deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(200, 198, 182, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: luminous-galaxy-orb 11.89s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(305deg, transparent 0 28%, rgba(147, 203, 211, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: luminous-galaxy-scan 13.48s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 71, 0.68), rgba(7, 115, 81, 0.7));
    border: 1px solid rgba(16, 96, 158, 0.88);
    box-shadow: 0 0 34px rgba(226, 141, 196, 1);
    backdrop-filter: blur(7.5px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(2.21deg) rotateY(1.37deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 141, 196, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 141, 196, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(226, 141, 196, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 141, 196, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(226, 141, 196, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(226, 141, 196, 1));
}

::selection {
    background: rgba(226, 141, 196, 1);
    color: rgba(182, 236, 172, 1);
}

@keyframes luminous-galaxy-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(0.66%, 2.29%, 0); }
    100% { transform: scale(1.12) translate3d(4.03%, -0.37%, 0); }
}

@keyframes luminous-galaxy-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes luminous-galaxy-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes luminous-galaxy-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
