custom_css:
/* Midnight Noir signature weave */
body {
    color: rgba(225, 236, 172, 1);
    background-color: rgba(8, 94, 73, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(187deg, rgba(133, 214, 133, 1) 0%, transparent 48%),
        conic-gradient(from 118deg at 50% 50%, rgba(225, 236, 172, 1), rgba(133, 214, 133, 1), rgba(198, 141, 226, 1), rgba(225, 236, 172, 1));
    animation: midnight-noir-bg 19.33s ease-in-out infinite;
    filter: hue-rotate(147deg) saturate(160%);
}

body::before {
    background: radial-gradient(circle at 31% 77%, rgba(214, 198, 194, 1.00) 0%, transparent 60%);
    clip-path: polygon(0% 12%, 35% 0%, 65% 8%, 100% 0%, 100% 88%, 68% 100%, 32% 92%, 0% 100%);
    animation: midnight-noir-mesh 19.14s ease-in-out infinite alternate;
    mix-blend-mode: hard-light;
    opacity: 0.38;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 45%, rgba(0, 0, 0, 0.55) 70%);
    mix-blend-mode: multiply;
    animation: midnight-noir-scan 9.31s linear infinite;
    opacity: 0.4;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(73, 6, 107, 0.68), rgba(7, 115, 7, 0.7));
    border: 1px solid rgba(16, 158, 122, 0.88);
    box-shadow: 0 0 34px rgba(133, 214, 133, 1);
    backdrop-filter: blur(10.8px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-2.99deg) rotateY(-1.27deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(225, 236, 172, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(225, 236, 172, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(133, 214, 133, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(225, 236, 172, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(133, 214, 133, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(133, 214, 133, 1));
}

::selection {
    background: rgba(225, 236, 172, 1);
    color: rgba(225, 236, 172, 1);
}

@keyframes midnight-noir-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(2.67%, -0.55%, 0); }
    100% { transform: scale(1.12) translate3d(3.17%, 1.56%, 0); }
}

@keyframes midnight-noir-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes midnight-noir-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes midnight-noir-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
