custom_css:
/* Midnight Pulsewave signature weave */
body {
    color: rgba(217, 172, 236, 1);
    background-color: rgba(94, 32, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 26% 2%, rgba(141, 226, 158, 1) 0%, transparent 58%),
        radial-gradient(circle at 39% 32%, rgba(141, 226, 158, 1) 0%, transparent 64%),
        conic-gradient(from 58deg, rgba(217, 172, 236, 1), rgba(141, 226, 158, 1), rgba(226, 176, 156, 1), rgba(214, 133, 171, 1), rgba(217, 172, 236, 1));
    animation: midnight-pulsewave-bg 15.48s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-86deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(187, 194, 205, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: midnight-pulsewave-orb 10.73s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(48deg, transparent 0 28%, rgba(221, 159, 162, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: midnight-pulsewave-scan 11.20s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 107, 26, 0.68), rgba(115, 7, 58, 0.7));
    border: 1px solid rgba(158, 56, 16, 0.88);
    box-shadow: 0 0 34px rgba(141, 226, 158, 1);
    backdrop-filter: blur(6.3px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.98deg) rotateY(-0.17deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(141, 226, 158, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(141, 226, 158, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(141, 226, 158, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(141, 226, 158, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(141, 226, 158, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(141, 226, 158, 1));
}

::selection {
    background: rgba(141, 226, 158, 1);
    color: rgba(217, 172, 236, 1);
}

@keyframes midnight-pulsewave-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(0.62%, 1.93%, 0); }
    100% { transform: scale(1.12) translate3d(-3.21%, -2.02%, 0); }
}

@keyframes midnight-pulsewave-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes midnight-pulsewave-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes midnight-pulsewave-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
