custom_css:
/* Midnight Spark signature weave */
body {
    color: rgba(236, 207, 172, 1);
    background-color: rgba(8, 94, 19, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(189deg, rgba(156, 226, 166, 1), transparent 45%),
        repeating-radial-gradient(circle, transparent 0 12px, rgba(200, 181, 194, 1.00) 12px 18px),
        linear-gradient(259deg, rgba(183, 214, 133, 1), rgba(183, 214, 133, 1), rgba(156, 226, 166, 1));
    animation: midnight-spark-bg 10.24s alternate infinite;
    background-size: cover, 280px 280px, cover;
    filter: saturate(170%) brightness(95%);
}

body::before {
    background: repeating-linear-gradient(343deg, transparent 0 22px, rgba(167, 221, 153, 1.00) 22px 44px);
    mask-image: radial-gradient(circle at center, rgba(0, 0, 0, 1) 0%, transparent 68%);
    animation: midnight-spark-mesh 15.49s alternate infinite;
    opacity: 0.3;
}

body::after {
    background: radial-gradient(circle at 8% 10%, rgba(156, 226, 166, 1) 0%, transparent 68%);
    mix-blend-mode: screen;
    animation: midnight-spark-orb 11.23s ease-in-out infinite;
    opacity: 0.28;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(11, 6, 107, 0.68), rgba(74, 115, 7, 0.7));
    border: 1px solid rgba(16, 158, 35, 0.88);
    box-shadow: 0 0 34px rgba(156, 226, 166, 1);
    backdrop-filter: blur(6.9px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.65deg) rotateY(0.33deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(183, 214, 133, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(183, 214, 133, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(156, 226, 166, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(183, 214, 133, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(156, 226, 166, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(156, 226, 166, 1));
}

::selection {
    background: rgba(183, 214, 133, 1);
    color: rgba(236, 207, 172, 1);
}

@keyframes midnight-spark-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(0.02%, -1.08%, 0); }
    100% { transform: scale(1.12) translate3d(3.05%, -1.41%, 0); }
}

@keyframes midnight-spark-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes midnight-spark-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes midnight-spark-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
