custom_css:
/* Mirage Embers signature weave */
body {
    color: rgba(172, 236, 177, 1);
    background-color: rgba(8, 38, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(110deg, transparent 0 40px, rgba(194, 198, 177, 1.00) 40px 80px),
        radial-gradient(circle at 42% 36%, rgba(226, 141, 178, 1) 0%, transparent 55%),
        linear-gradient(351deg, rgba(133, 214, 206, 1), rgba(156, 181, 226, 1));
    animation: mirage-embers-bg 22.04s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 87deg, transparent 0 25%, rgba(156, 181, 226, 1) 45%, transparent 75%);
    animation: mirage-embers-mesh 21.67s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: mirage-embers-scan 16.94s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 49, 0.68), rgba(7, 115, 104, 0.7));
    border: 1px solid rgba(16, 65, 158, 0.88);
    box-shadow: 0 0 34px rgba(156, 181, 226, 1);
    backdrop-filter: blur(5.5px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.88deg) rotateY(1.31deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 141, 178, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 141, 178, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(156, 181, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 141, 178, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(156, 181, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(156, 181, 226, 1));
}

::selection {
    background: rgba(226, 141, 178, 1);
    color: rgba(172, 236, 177, 1);
}

@keyframes mirage-embers-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(2.50%, 1.25%, 0); }
    100% { transform: scale(1.12) translate3d(4.14%, -1.00%, 0); }
}

@keyframes mirage-embers-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes mirage-embers-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes mirage-embers-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
