custom_css:
/* Nebula Flux signature weave */
body {
    color: rgba(236, 172, 184, 1);
    background-color: rgba(60, 94, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(228deg, rgba(198, 226, 156, 1), transparent 45%),
        repeating-radial-gradient(circle, transparent 0 12px, rgba(198, 183, 201, 1.00) 12px 18px),
        linear-gradient(67deg, rgba(198, 226, 156, 1), rgba(214, 186, 133, 1), rgba(141, 199, 226, 1));
    animation: nebula-flux-bg 17.37s alternate infinite;
    background-size: cover, 280px 280px, cover;
    filter: saturate(170%) brightness(95%);
}

body::before {
    background: repeating-linear-gradient(284deg, transparent 0 22px, rgba(204, 210, 147, 1.00) 22px 44px);
    mask-image: radial-gradient(circle at center, rgba(0, 0, 0, 1) 0%, transparent 68%);
    animation: nebula-flux-mesh 10.46s alternate infinite;
    opacity: 0.3;
}

body::after {
    background: radial-gradient(circle at 28% 81%, rgba(141, 199, 226, 1) 0%, transparent 68%);
    mix-blend-mode: screen;
    animation: nebula-flux-orb 7.67s ease-in-out infinite;
    opacity: 0.28;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 75, 107, 0.68), rgba(115, 77, 7, 0.7));
    border: 1px solid rgba(101, 158, 16, 0.88);
    box-shadow: 0 0 34px rgba(141, 199, 226, 1);
    backdrop-filter: blur(10.0px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(0.17deg) rotateY(-0.90deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(198, 226, 156, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(198, 226, 156, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(141, 199, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(198, 226, 156, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(141, 199, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(141, 199, 226, 1));
}

::selection {
    background: rgba(198, 226, 156, 1);
    color: rgba(236, 172, 184, 1);
}

@keyframes nebula-flux-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-1.52%, 1.35%, 0); }
    100% { transform: scale(1.12) translate3d(2.33%, 0.73%, 0); }
}

@keyframes nebula-flux-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes nebula-flux-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes nebula-flux-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
