custom_css:
:root {
  --nebula-haze: radial-gradient(circle at 20% 20%, rgba(64, 184, 255, 0.22), transparent 60%), radial-gradient(circle at 80% 30%, rgba(255, 214, 102, 0.18), transparent 70%);
}
body {
  background: linear-gradient(170deg, #040d1e 0%, #051a3a 45%, #0b284d 100%);
  color: #e6f5ff;
}
main {
  backdrop-filter: blur(16px);
  background: rgba(7, 18, 43, 0.7);
}
.mes, .mes_text {
  background: rgba(12, 33, 74, 0.7);
  border: 1px solid rgba(137, 226, 255, 0.35);
  box-shadow: 0 0 24px rgba(17, 94, 176, 0.35);
  position: relative;
}
.mes::before {
  content: "";
  position: absolute;
  inset: -12%;
  background: var(--nebula-haze);
  z-index: -1;
  filter: blur(18px);
  opacity: 0.9;
}
#send_form textarea {
  background: rgba(10, 24, 51, 0.85);
  border: 1px solid rgba(137, 226, 255, 0.35);
  box-shadow: 0 0 18px rgba(64, 184, 255, 0.3);
}
