custom_css:
/* Obsidian Spectrum signature weave */
body {
    color: rgba(172, 236, 195, 1);
    background-color: rgba(8, 13, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        conic-gradient(from 228deg, rgba(172, 236, 195, 1), rgba(156, 161, 226, 1), rgba(226, 141, 154, 1), rgba(133, 199, 214, 1), rgba(172, 236, 195, 1)),
        radial-gradient(circle at 41% 3%, rgba(133, 199, 214, 1) 0%, transparent 70%);
    animation: obsidian-spectrum-bg 22.39s ease-in-out infinite;
    filter: saturate(150%) brightness(105%);
}

body::before {
    background: repeating-conic-gradient(from 344deg, transparent 0 15deg, rgba(194, 198, 179, 1.00) 15deg 30deg);
    animation: obsidian-spectrum-mesh 11.86s linear infinite;
    mix-blend-mode: color-dodge;
    opacity: 0.24;
}

body::after {
    background: radial-gradient(circle at center, transparent 0 35%, rgba(0, 0, 0, 0.55) 60%);
    backdrop-filter: blur(8px) saturate(190%);
    animation: obsidian-spectrum-scan 11.29s ease-in-out infinite;
    opacity: 0.36;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 21, 0.68), rgba(7, 95, 115, 0.7));
    border: 1px solid rgba(16, 25, 158, 0.88);
    box-shadow: 0 0 34px rgba(133, 199, 214, 1);
    backdrop-filter: blur(7.0px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.55deg) rotateY(0.89deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 161, 226, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 161, 226, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(133, 199, 214, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 161, 226, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(133, 199, 214, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(133, 199, 214, 1));
}

::selection {
    background: rgba(156, 161, 226, 1);
    color: rgba(172, 236, 195, 1);
}

@keyframes obsidian-spectrum-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(0.94%, 1.83%, 0); }
    100% { transform: scale(1.12) translate3d(-1.72%, -4.72%, 0); }
}

@keyframes obsidian-spectrum-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes obsidian-spectrum-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes obsidian-spectrum-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
