custom_css:
/* Phantom Reverie signature weave */
body {
    color: rgba(176, 236, 172, 1);
    background-color: rgba(8, 48, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 35% 81%, rgba(226, 141, 188, 1) 0%, transparent 58%),
        radial-gradient(circle at 1% 54%, rgba(133, 214, 197, 1) 0%, transparent 64%),
        conic-gradient(from 263deg, rgba(176, 236, 172, 1), rgba(226, 141, 188, 1), rgba(156, 189, 226, 1), rgba(133, 214, 197, 1), rgba(176, 236, 172, 1));
    animation: phantom-reverie-bg 10.96s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-154deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(196, 198, 178, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: phantom-reverie-orb 11.81s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(54deg, transparent 0 28%, rgba(147, 199, 214, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: phantom-reverie-scan 13.25s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 61, 0.68), rgba(7, 115, 92, 0.7));
    border: 1px solid rgba(16, 82, 158, 0.88);
    box-shadow: 0 0 34px rgba(133, 214, 197, 1);
    backdrop-filter: blur(5.3px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(2.56deg) rotateY(1.57deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(226, 141, 188, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(226, 141, 188, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(133, 214, 197, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(226, 141, 188, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(133, 214, 197, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(133, 214, 197, 1));
}

::selection {
    background: rgba(226, 141, 188, 1);
    color: rgba(176, 236, 172, 1);
}

@keyframes phantom-reverie-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-1.28%, -0.02%, 0); }
    100% { transform: scale(1.12) translate3d(4.05%, 3.28%, 0); }
}

@keyframes phantom-reverie-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes phantom-reverie-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes phantom-reverie-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
