custom_css:
/* Quantum Sanctuary signature weave */
body {
    color: rgba(236, 222, 172, 1);
    background-color: rgba(8, 94, 39, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 18% 21%, rgba(156, 226, 182, 1) 0%, transparent 58%),
        radial-gradient(circle at 3% 22%, rgba(156, 226, 182, 1) 0%, transparent 64%),
        conic-gradient(from 96deg, rgba(236, 222, 172, 1), rgba(165, 141, 226, 1), rgba(156, 226, 182, 1), rgba(164, 214, 133, 1), rgba(236, 222, 172, 1));
    animation: quantum-sanctuary-bg 21.00s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-3deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(208, 190, 194, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: quantum-sanctuary-orb 27.32s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(25deg, transparent 0 28%, rgba(159, 221, 162, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: quantum-sanctuary-scan 7.81s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(34, 6, 107, 0.68), rgba(49, 115, 7, 0.7));
    border: 1px solid rgba(16, 158, 68, 0.88);
    box-shadow: 0 0 34px rgba(156, 226, 182, 1);
    backdrop-filter: blur(9.3px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.59deg) rotateY(-0.94deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 226, 182, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 226, 182, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(156, 226, 182, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 226, 182, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(156, 226, 182, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(156, 226, 182, 1));
}

::selection {
    background: rgba(156, 226, 182, 1);
    color: rgba(236, 222, 172, 1);
}

@keyframes quantum-sanctuary-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.85%, -1.31%, 0); }
    100% { transform: scale(1.12) translate3d(1.96%, -0.82%, 0); }
}

@keyframes quantum-sanctuary-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes quantum-sanctuary-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes quantum-sanctuary-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
