custom_css:
/* Retro Embers signature weave */
body {
    color: rgba(192, 172, 236, 1);
    background-color: rgba(94, 8, 16, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        conic-gradient(from 31deg, rgba(192, 172, 236, 1), rgba(156, 226, 141, 1), rgba(156, 226, 141, 1), rgba(214, 133, 202, 1), rgba(192, 172, 236, 1)),
        radial-gradient(circle at 10% 73%, rgba(214, 133, 202, 1) 0%, transparent 70%);
    animation: retro-embers-bg 13.20s ease-in-out infinite;
    filter: saturate(150%) brightness(105%);
}

body::before {
    background: repeating-conic-gradient(from 298deg, transparent 0 15deg, rgba(178, 194, 198, 1.00) 15deg 30deg);
    animation: retro-embers-mesh 16.85s linear infinite;
    mix-blend-mode: color-dodge;
    opacity: 0.24;
}

body::after {
    background: radial-gradient(circle at center, transparent 0 35%, rgba(0, 0, 0, 0.55) 60%);
    backdrop-filter: blur(8px) saturate(190%);
    animation: retro-embers-scan 18.66s ease-in-out infinite;
    opacity: 0.36;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(24, 107, 6, 0.68), rgba(115, 7, 99, 0.7));
    border: 1px solid rgba(158, 16, 30, 0.88);
    box-shadow: 0 0 34px rgba(214, 133, 202, 1);
    backdrop-filter: blur(6.2px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.12deg) rotateY(0.73deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 226, 141, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 226, 141, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(214, 133, 202, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 226, 141, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(214, 133, 202, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(214, 133, 202, 1));
}

::selection {
    background: rgba(156, 226, 141, 1);
    color: rgba(192, 172, 236, 1);
}

@keyframes retro-embers-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.15%, 0.25%, 0); }
    100% { transform: scale(1.12) translate3d(2.95%, 4.63%, 0); }
}

@keyframes retro-embers-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes retro-embers-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes retro-embers-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
