custom_css:
/* Solaris Drift signature weave */
body {
    color: rgba(172, 196, 236, 1);
    background-color: rgba(94, 8, 76, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        linear-gradient(333deg, rgba(226, 156, 211, 1), transparent 45%),
        repeating-radial-gradient(circle, transparent 0 12px, rgba(189, 208, 198, 1.00) 12px 18px),
        linear-gradient(166deg, rgba(172, 196, 236, 1), rgba(171, 133, 214, 1), rgba(171, 133, 214, 1));
    animation: solaris-drift-bg 22.81s alternate infinite;
    background-size: cover, 280px 280px, cover;
    filter: saturate(170%) brightness(95%);
}

body::before {
    background: repeating-linear-gradient(246deg, transparent 0 22px, rgba(204, 147, 212, 1.00) 22px 44px);
    mask-image: radial-gradient(circle at center, rgba(0, 0, 0, 1) 0%, transparent 68%);
    animation: solaris-drift-mesh 24.77s alternate infinite;
    opacity: 0.3;
}

body::after {
    background: radial-gradient(circle at 70% 12%, rgba(171, 133, 214, 1) 0%, transparent 68%);
    mix-blend-mode: screen;
    animation: solaris-drift-orb 8.84s ease-in-out infinite;
    opacity: 0.28;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(93, 107, 6, 0.68), rgba(58, 7, 115, 0.7));
    border: 1px solid rgba(158, 16, 127, 0.88);
    box-shadow: 0 0 34px rgba(171, 133, 214, 1);
    backdrop-filter: blur(10.8px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(2.11deg) rotateY(-1.54deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(172, 196, 236, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(172, 196, 236, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(171, 133, 214, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(172, 196, 236, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(171, 133, 214, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(171, 133, 214, 1));
}

::selection {
    background: rgba(172, 196, 236, 1);
    color: rgba(172, 196, 236, 1);
}

@keyframes solaris-drift-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-1.42%, 2.95%, 0); }
    100% { transform: scale(1.12) translate3d(1.03%, 0.63%, 0); }
}

@keyframes solaris-drift-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes solaris-drift-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes solaris-drift-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
