custom_css:
/* Spectrum Reactor signature weave */
body {
    color: rgba(228, 236, 172, 1);
    background-color: rgba(8, 94, 68, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        conic-gradient(from 201deg, rgba(228, 236, 172, 1), rgba(156, 226, 205, 1), rgba(194, 141, 226, 1), rgba(137, 214, 133, 1), rgba(228, 236, 172, 1)),
        radial-gradient(circle at 36% 70%, rgba(228, 236, 172, 1) 0%, transparent 70%);
    animation: spectrum-reactor-bg 8.47s ease-in-out infinite;
    filter: saturate(150%) brightness(105%);
}

body::before {
    background: repeating-conic-gradient(from 251deg, transparent 0 15deg, rgba(214, 198, 194, 1.00) 15deg 30deg);
    animation: spectrum-reactor-mesh 27.45s linear infinite;
    mix-blend-mode: color-dodge;
    opacity: 0.24;
}

body::after {
    background: radial-gradient(circle at center, transparent 0 35%, rgba(0, 0, 0, 0.55) 60%);
    backdrop-filter: blur(8px) saturate(190%);
    animation: spectrum-reactor-scan 13.85s ease-in-out infinite;
    opacity: 0.36;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(68, 6, 107, 0.68), rgba(13, 115, 7, 0.7));
    border: 1px solid rgba(16, 158, 115, 0.88);
    box-shadow: 0 0 34px rgba(228, 236, 172, 1);
    backdrop-filter: blur(5.5px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.81deg) rotateY(-1.00deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 226, 205, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 226, 205, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(228, 236, 172, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 226, 205, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(228, 236, 172, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(228, 236, 172, 1));
}

::selection {
    background: rgba(156, 226, 205, 1);
    color: rgba(228, 236, 172, 1);
}

@keyframes spectrum-reactor-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(2.14%, -2.51%, 0); }
    100% { transform: scale(1.12) translate3d(0.68%, 2.80%, 0); }
}

@keyframes spectrum-reactor-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes spectrum-reactor-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes spectrum-reactor-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
