custom_css:
body {
  background: linear-gradient(115deg, #08001f 0%, #14004d 40%, #30006f 75%, #ff008c 100%);
  color: #ffe1ff;
}
.header, .tray {
  background: rgba(12, 0, 48, 0.75);
  backdrop-filter: blur(14px);
  border-bottom: 1px solid rgba(255, 0, 140, 0.35);
}
.chat_wrapper {
  background: rgba(10, 0, 36, 0.55);
  border-left: 1px solid rgba(255, 114, 182, 0.25);
}
.mes, .mes_text {
  background: rgba(29, 0, 74, 0.72);
  border: 1px solid rgba(255, 233, 120, 0.4);
  box-shadow: 0 14px 28px rgba(10, 0, 40, 0.55);
}
.mes .mes_block:nth-child(odd) {
  background: linear-gradient(135deg, rgba(255, 0, 140, 0.12), transparent);
}
button, .menu_button {
  background: linear-gradient(135deg, rgba(255, 114, 182, 0.25), rgba(255, 233, 120, 0.15));
  border: 1px solid rgba(255, 114, 182, 0.45);
  box-shadow: 0 0 14px rgba(255, 0, 140, 0.35);
  color: #ffe1ff;
}
button:hover, .menu_button:hover {
  filter: brightness(1.15);
}
#send_form textarea {
  background: rgba(8, 0, 34, 0.85);
  border: 1px solid rgba(255, 114, 182, 0.35);
  color: #ffe1ff;
}
::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, #ff007a, #ffae00);
}
