custom_css:
/* Turbo Haze signature weave */
body {
    color: rgba(236, 172, 223, 1);
    background-color: rgba(94, 76, 8, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 37% 35%, rgba(141, 226, 201, 1) 0%, transparent 58%),
        radial-gradient(circle at 11% 2%, rgba(214, 135, 133, 1) 0%, transparent 64%),
        conic-gradient(from 279deg, rgba(236, 172, 223, 1), rgba(141, 226, 201, 1), rgba(226, 211, 156, 1), rgba(214, 135, 133, 1), rgba(236, 172, 223, 1));
    animation: turbo-haze-bg 25.18s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-115deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(198, 194, 214, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: turbo-haze-orb 10.46s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(62deg, transparent 0 28%, rgba(221, 181, 147, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: turbo-haze-scan 11.33s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(6, 107, 76, 0.68), rgba(115, 11, 7, 0.7));
    border: 1px solid rgba(158, 127, 16, 0.88);
    box-shadow: 0 0 34px rgba(214, 135, 133, 1);
    backdrop-filter: blur(11.5px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.30deg) rotateY(-2.20deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(141, 226, 201, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(141, 226, 201, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(214, 135, 133, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(141, 226, 201, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(214, 135, 133, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(214, 135, 133, 1));
}

::selection {
    background: rgba(141, 226, 201, 1);
    color: rgba(236, 172, 223, 1);
}

@keyframes turbo-haze-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-0.48%, -2.36%, 0); }
    100% { transform: scale(1.12) translate3d(-3.69%, -0.35%, 0); }
}

@keyframes turbo-haze-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes turbo-haze-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes turbo-haze-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
