custom_css:
/* Turbo Pulsegrid signature weave */
body {
    color: rgba(179, 236, 172, 1);
    background-color: rgba(8, 52, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        repeating-linear-gradient(301deg, transparent 0 40px, rgba(198, 198, 180, 1.00) 40px 80px),
        radial-gradient(circle at 59% 80%, rgba(156, 192, 226, 1) 0%, transparent 55%),
        linear-gradient(96deg, rgba(133, 214, 192, 1), rgba(156, 192, 226, 1));
    animation: turbo-pulsegrid-bg 12.33s ease-in-out infinite alternate;
    background-size: 120% 120%, cover, cover;
    filter: contrast(110%) saturate(130%);
}

body::before {
    background: conic-gradient(from 71deg, transparent 0 25%, rgba(156, 192, 226, 1) 45%, transparent 75%);
    animation: turbo-pulsegrid-mesh 23.18s linear infinite;
    mix-blend-mode: lighten;
    opacity: 0.25;
}

body::after {
    background: radial-gradient(ellipse at center, transparent 0 40%, rgba(0, 0, 0, 0.4) 70%, transparent 100%);
    backdrop-filter: blur(6px) saturate(180%);
    mix-blend-mode: soft-light;
    animation: turbo-pulsegrid-scan 7.28s linear infinite;
    opacity: 0.35;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 66, 0.68), rgba(7, 115, 86, 0.7));
    border: 1px solid rgba(16, 89, 158, 0.88);
    box-shadow: 0 0 34px rgba(156, 192, 226, 1);
    backdrop-filter: blur(5.1px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-1.44deg) rotateY(2.26deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 192, 226, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 192, 226, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(156, 192, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 192, 226, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(156, 192, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(156, 192, 226, 1));
}

::selection {
    background: rgba(156, 192, 226, 1);
    color: rgba(179, 236, 172, 1);
}

@keyframes turbo-pulsegrid-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-1.55%, 0.44%, 0); }
    100% { transform: scale(1.12) translate3d(3.45%, -1.06%, 0); }
}

@keyframes turbo-pulsegrid-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes turbo-pulsegrid-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes turbo-pulsegrid-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
