custom_css:
/* Velvet Prism signature weave */
body {
    color: rgba(172, 236, 173, 1);
    background-color: rgba(8, 42, 94, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 35% 22%, rgba(133, 214, 202, 1) 0%, transparent 58%),
        radial-gradient(circle at 30% 34%, rgba(226, 141, 182, 1) 0%, transparent 64%),
        conic-gradient(from 63deg, rgba(172, 236, 173, 1), rgba(226, 141, 182, 1), rgba(156, 184, 226, 1), rgba(133, 214, 202, 1), rgba(172, 236, 173, 1));
    animation: velvet-prism-bg 20.15s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-128deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(194, 198, 177, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: velvet-prism-orb 27.04s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(30deg, transparent 0 28%, rgba(147, 196, 216, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: velvet-prism-scan 14.34s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(107, 6, 54, 0.68), rgba(7, 115, 99, 0.7));
    border: 1px solid rgba(16, 72, 158, 0.88);
    box-shadow: 0 0 34px rgba(226, 141, 182, 1);
    backdrop-filter: blur(6.1px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(2.22deg) rotateY(-0.28deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(133, 214, 202, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(133, 214, 202, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(226, 141, 182, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(133, 214, 202, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(226, 141, 182, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(226, 141, 182, 1));
}

::selection {
    background: rgba(133, 214, 202, 1);
    color: rgba(172, 236, 173, 1);
}

@keyframes velvet-prism-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(0.26%, 1.12%, 0); }
    100% { transform: scale(1.12) translate3d(2.13%, -3.27%, 0); }
}

@keyframes velvet-prism-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes velvet-prism-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes velvet-prism-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
