custom_css:
body {
  background: radial-gradient(circle at 20% 20%, rgba(70, 255, 209, 0.18), transparent 60%), radial-gradient(circle at 80% 10%, rgba(255, 148, 226, 0.22), transparent 65%), #030c09;
  color: #e2f5ef;
}
main, .tray {
  backdrop-filter: blur(18px);
  background: rgba(6, 20, 16, 0.72);
}
.mes, .mes_text {
  background: linear-gradient(135deg, rgba(12, 36, 30, 0.88), rgba(6, 18, 14, 0.88));
  border: 1px solid rgba(70, 255, 209, 0.32);
  box-shadow: 0 18px 40px rgba(0, 0, 0, 0.6);
  position: relative;
  overflow: hidden;
}
.mes::after {
  content: "";
  position: absolute;
  inset: -40%;
  background: radial-gradient(circle, rgba(255, 148, 226, 0.3) 0%, transparent 60%);
  mix-blend-mode: screen;
  opacity: 0.7;
  animation: bloomPulse 12s ease-in-out infinite;
}
button {
  background: linear-gradient(135deg, rgba(255, 216, 102, 0.2), rgba(70, 255, 209, 0.25));
  border: 1px solid rgba(70, 255, 209, 0.4);
  color: #e2f5ef;
}
button:hover {
  box-shadow: 0 0 18px rgba(70, 255, 209, 0.4);
}
#send_form textarea {
  background: rgba(5, 18, 15, 0.85);
  border: 1px solid rgba(255, 148, 226, 0.35);
  color: #e2f5ef;
}
@keyframes bloomPulse {
  0%, 100% { transform: rotate(0deg) scale(1); opacity: 0.6; }
  50% { transform: rotate(12deg) scale(1.2); opacity: 0.9; }
}
