custom_css:
/* Zenith Lattice signature weave */
body {
    color: rgba(236, 232, 172, 1);
    background-color: rgba(8, 94, 54, 0.7);
    background-attachment: fixed;
    overflow-x: hidden;
}

body::before,
body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
}

body {
    background-image:
        radial-gradient(circle at 46% 41%, rgba(156, 226, 194, 1) 0%, transparent 58%),
        radial-gradient(circle at 52% 54%, rgba(179, 141, 226, 1) 0%, transparent 64%),
        conic-gradient(from 120deg, rgba(236, 232, 172, 1), rgba(179, 141, 226, 1), rgba(156, 226, 194, 1), rgba(150, 214, 133, 1), rgba(236, 232, 172, 1));
    animation: zenith-lattice-bg 15.05s ease-in-out infinite alternate;
    backdrop-filter: saturate(145%) hue-rotate(-107deg);
}

body::before {
    inset: -12vh;
    background: radial-gradient(circle, rgba(213, 196, 194, 1.00) 0%, transparent 64%);
    filter: blur(48px);
    mix-blend-mode: screen;
    animation: zenith-lattice-orb 20.36s ease-in-out infinite alternate;
    opacity: 0.32;
}

body::after {
    background: linear-gradient(333deg, transparent 0 28%, rgba(154, 221, 170, 1.00) 48%, transparent 72%);
    mix-blend-mode: color-dodge;
    animation: zenith-lattice-scan 11.97s linear infinite;
    opacity: 0.22;
}

.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(51, 6, 107, 0.68), rgba(31, 115, 7, 0.7));
    border: 1px solid rgba(16, 158, 91, 0.88);
    box-shadow: 0 0 34px rgba(179, 141, 226, 1);
    backdrop-filter: blur(11.1px) saturate(180%);
    transform-style: preserve-3d;
    transition: transform 0.6s ease, box-shadow 0.6s ease;
}

.mes:hover {
    transform: translateY(-6px) rotateX(-0.39deg) rotateY(2.98deg) scale(1.02);
    box-shadow: 0 18px 48px rgba(156, 226, 194, 1);
}

.chat_mes_block .mes_text p code {
    background: rgba(0, 0, 0, 0.45);
    color: rgba(156, 226, 194, 1);
    border-radius: 8px;
    padding: 2px 6px;
    box-shadow: 0 0 18px rgba(179, 141, 226, 1);
}

.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(156, 226, 194, 1);
    background: rgba(0, 0, 0, 0.32);
    box-shadow: inset 0 0 18px rgba(179, 141, 226, 1);
}

.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(179, 141, 226, 1));
}

::selection {
    background: rgba(156, 226, 194, 1);
    color: rgba(236, 232, 172, 1);
}

@keyframes zenith-lattice-bg {
    0% { transform: scale(1) translate3d(0, 0, 0); }
    50% { transform: scale(1.06) translate3d(-2.51%, -1.38%, 0); }
    100% { transform: scale(1.12) translate3d(-4.60%, -1.43%, 0); }
}

@keyframes zenith-lattice-orb {
    0% { transform: translate3d(-3%, -3%, 0) scale(1); opacity: 0.18; }
    50% { transform: translate3d(4%, 5%, 0) scale(1.08); opacity: 0.36; }
    100% { transform: translate3d(-2%, 6%, 0) scale(1.02); opacity: 0.24; }
}

@keyframes zenith-lattice-scan {
    0% { transform: rotate(0deg); opacity: 0.12; }
    50% { opacity: 0.28; }
    100% { transform: rotate(360deg); opacity: 0.12; }
}

@keyframes zenith-lattice-mesh {
    0% { transform: translateY(-4%) scale(1); opacity: 0.2; }
    50% { transform: translateY(4%) scale(1.06); opacity: 0.32; }
    100% { transform: translateY(-6%) scale(1.02); opacity: 0.24; }
}
