/* === Firey Theme with Rising Embers === */

body {
    color: rgba(255, 220, 200, 1);
    background-color: #0d0000;
    background-image:
        radial-gradient(circle at 25% 40%, rgba(255, 80, 0, 0.25) 0%, transparent 60%),
        radial-gradient(circle at 75% 60%, rgba(255, 40, 0, 0.3) 0%, transparent 65%),
        linear-gradient(180deg, #1a0000, #0a0000 70%, #000000);
    background-attachment: fixed;
    background-size: cover;
    animation: firey-bg 20s ease-in-out infinite alternate;
    overflow-x: hidden;
}

/* Ember particle layers */
body::before, body::after {
    content: '';
    position: fixed;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    background-repeat: repeat;
    background-size: 4px 4px;
    mix-blend-mode: screen;
}

/* Large glowing embers */
body::before {
    background-image: radial-gradient(rgba(255,120,60,0.4) 0%, transparent 60%);
    animation: embers-rise 18s linear infinite;
    opacity: 0.35;
}

/* Small faint embers */
body::after {
    background-image: radial-gradient(rgba(255,80,30,0.25) 0%, transparent 70%);
    animation: embers-rise 28s linear infinite;
    opacity: 0.25;
}

/* Message bubbles like molten obsidian */
.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(30, 0, 0, 0.85), rgba(60, 0, 0, 0.9));
    border: 1px solid rgba(200, 60, 30, 0.9);
    box-shadow: 0 0 20px rgba(255, 80, 0, 0.4);
    backdrop-filter: blur(8px) saturate(160%);
    border-radius: 12px;
    transition: transform 0.5s ease, box-shadow 0.5s ease;
    animation: appear 0.6s ease forwards;
}

.mes:hover {
    transform: translateY(-6px) scale(1.03);
    box-shadow: 0 0 30px rgba(255, 100, 0, 0.8);
}

/* Code blocks = glowing runes */
.chat_mes_block .mes_text p code {
    background: rgba(20, 0, 0, 0.6);
    color: rgba(255, 120, 120, 1);
    border-radius: 6px;
    padding: 2px 6px;
    box-shadow: 0 0 10px rgba(255, 60, 0, 0.8);
}

/* Quotes = charred embers */
.chat_mes_block .mes_text blockquote {
    border-left: 3px solid rgba(255, 120, 60, 0.8);
    background: rgba(0, 0, 0, 0.4);
    box-shadow: inset 0 0 10px rgba(255, 80, 0, 0.6);
    padding-left: 10px;
    border-radius: 4px;
}

/* Avatar infernal glow */
.mes .avatar {
    filter: drop-shadow(0 0 12px rgba(255, 50, 0, 0.9));
}

/* Text highlight = fire spark */
::selection {
    background: rgba(255, 100, 0, 0.8);
    color: rgba(255, 240, 220, 1);
}

/* === Animations === */
@keyframes firey-bg {
    0% { background-position: 0% 0%; filter: hue-rotate(0deg); }
    100% { background-position: 100% 100%; filter: hue-rotate(-15deg); }
}

@keyframes embers-rise {
    0% { background-position: 0% 100%; }
    100% { background-position: 0% 0%; }
}

@keyframes appear {
    from { opacity: 0; transform: translateY(10px) scale(0.97); filter: blur(4px); }
    to   { opacity: 1; transform: translateY(0) scale(1); filter: blur(0); }
}
