custom_css:
/* === Icy Theme - True Frost Effects === */

body {
    color: rgba(200, 220, 240, 1);
    background: 
        radial-gradient(ellipse at 20% 30%, rgba(0, 100, 200, 0.15) 0%, transparent 50%),
        radial-gradient(ellipse at 80% 70%, rgba(0, 150, 255, 0.1) 0%, transparent 60%),
        linear-gradient(135deg, #0a1a2a 0%, #001122 50%, #000a1a 100%);
    background-attachment: fixed;
    background-size: 100% 100%, 100% 100%, 100% 100%;
    animation: winter-breeze 30s ease-in-out infinite;
    overflow-x: hidden;
    position: relative;
}

/* Snowflake pattern overlay */
body::before {
    content: '';
    position: fixed;
    inset: 0;
    background-image: 
        radial-gradient(circle at 20% 20%, rgba(255, 255, 255, 0.03) 1px, transparent 1px),
        radial-gradient(circle at 40% 40%, rgba(200, 220, 255, 0.02) 1px, transparent 1px),
        radial-gradient(circle at 60% 60%, rgba(150, 200, 255, 0.03) 1px, transparent 1px),
        radial-gradient(circle at 80% 80%, rgba(100, 180, 255, 0.02) 1px, transparent 1px);
    background-size: 50px 50px, 80px 80px, 60px 60px, 70px 70px;
    animation: snowfall 20s linear infinite;
    pointer-events: none;
    z-index: 1;
}

/* Ice crystal formations */
body::after {
    content: '';
    position: fixed;
    inset: 0;
    background: 
        conic-gradient(from 0deg at 10% 20%, transparent 0deg, rgba(0, 150, 255, 0.05) 30deg, transparent 60deg),
        conic-gradient(from 45deg at 70% 30%, transparent 0deg, rgba(100, 200, 255, 0.03) 20deg, transparent 40deg),
        conic-gradient(from 90deg at 30% 80%, transparent 0deg, rgba(0, 200, 255, 0.04) 25deg, transparent 50deg);
    animation: ice-crystals 25s linear infinite;
    pointer-events: none;
    z-index: 1;
}

/* Frozen message bubbles with ice texture */
.mes, .mesText, .swipe-message {
    background: 
        linear-gradient(135deg, rgba(20, 40, 80, 0.9), rgba(30, 50, 90, 0.85)),
        repeating-linear-gradient(45deg, transparent 0px, rgba(255, 255, 255, 0.02) 1px, transparent 2px);
    border: 2px solid rgba(100, 150, 200, 0.8);
    box-shadow: 
        0 0 20px rgba(0, 150, 255, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.1),
        0 4px 15px rgba(0, 100, 200, 0.2);
    backdrop-filter: blur(8px) saturate(120%);
    border-radius: 15px;
    transition: all 0.4s ease;
    animation: frost-appear 0.8s ease forwards;
    position: relative;
    z-index: 2;
}

.mes:hover {
    transform: translateY(-4px) scale(1.01);
    box-shadow: 
        0 0 30px rgba(0, 200, 255, 0.5),
        inset 0 1px 0 rgba(255, 255, 255, 0.2),
        0 6px 20px rgba(0, 100, 200, 0.3);
    background: 
        linear-gradient(135deg, rgba(30, 50, 90, 0.9), rgba(40, 60, 100, 0.85)),
        repeating-linear-gradient(45deg, transparent 0px, rgba(255, 255, 255, 0.03) 1px, transparent 2px);
}

/* Frost shimmer effect on hover */
.mes:hover::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(110deg, transparent 40%, rgba(255, 255, 255, 0.1) 50%, transparent 60%);
    animation: frost-shimmer 1.5s ease-in-out;
    pointer-events: none;
    border-radius: 15px;
}

/* Frozen code blocks with ice runes */
.chat_mes_block .mes_text p code {
    background: rgba(10, 30, 60, 0.8);
    color: rgba(150, 200, 255, 1);
    border-radius: 8px;
    padding: 3px 8px;
    border: 1px solid rgba(100, 150, 200, 0.6);
    box-shadow: 
        0 0 8px rgba(0, 150, 255, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
    font-family: 'Courier New', monospace;
    text-shadow: 0 0 4px rgba(0, 150, 255, 0.6);
}

/* Ice crystal quotes */
.chat_mes_block .mes_text blockquote {
    border-left: 4px solid rgba(100, 150, 200, 0.8);
    background: rgba(0, 20, 40, 0.4);
    box-shadow: 
        inset 0 0 15px rgba(0, 150, 255, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
    padding-left: 15px;
    border-radius: 8px;
    position: relative;
}

.chat_mes_block .mes_text blockquote::before {
    content: '‚ùÑ'; 
    font-size: 1.5em;
    color: rgba(150, 200, 255, 0.8);
    position: absolute;
    left: 5px;
    top: -5px;
    animation: snowflake-float 3s ease-in-out infinite;
}

/* Frozen avatar glow */
.mes .avatar {
    filter: drop-shadow(0 0 15px rgba(0, 150, 255, 0.6)) brightness(1.1);
    transition: filter 0.3s ease;
}

.mes:hover .avatar {
    filter: drop-shadow(0 0 20px rgba(0, 200, 255, 0.8)) brightness(1.2);
}

/* Ice spark selection */
::selection {
    background: rgba(0, 150, 255, 0.4);
    color: rgba(240, 250, 255, 1);
    text-shadow: 0 0 4px rgba(0, 150, 255, 0.8);
}

/* === Unique Ice Animations === */
@keyframes winter-breeze {
    0% { background-position: 0% 0%, 0% 0%, 0% 0%; }
    25% { background-position: 10% 5%, 5% 10%, 0% 0%; }
    50% { background-position: 5% 10%, 10% 5%, 0% 0%; }
    75% { background-position: 15% 5%, 5% 15%, 0% 0%; }
    100% { background-position: 0% 0%, 0% 0%, 0% 0%; }
}

@keyframes snowfall {
    0% { transform: translateY(-100vh) rotate(0deg); opacity: 0; }
    10% { opacity: 1; }
    90% { opacity: 1; }
    100% { transform: translateY(100vh) rotate(360deg); opacity: 0; }
}

@keyframes ice-crystals {
    0% { transform: rotate(0deg) scale(1); opacity: 0.3; }
    50% { transform: rotate(180deg) scale(1.1); opacity: 0.6; }
    100% { transform: rotate(360deg) scale(1); opacity: 0.3; }
}

@keyframes frost-appear {
    from { 
        opacity: 0; 
        transform: translateY(15px) scale(0.95); 
        filter: blur(6px); 
    }
    to { 
        opacity: 1; 
        transform: translateY(0) scale(1); 
        filter: blur(0); 
    }
}

@keyframes frost-shimmer {
    0% { transform: translateX(-100%) skewX(-15deg); }
    100% { transform: translateX(100%) skewX(-15deg); }
}

@keyframes snowflake-float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-3px) rotate(180deg); }
}

/* Ice-themed scrollbar with frost */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    background: rgba(20, 40, 80, 0.6);
    border-radius: 6px;
    border: 1px solid rgba(100, 150, 200, 0.3);
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, rgba(100, 150, 200, 0.8), rgba(150, 200, 255, 0.6));
    border-radius: 6px;
    border: 1px solid rgba(200, 220, 255, 0.4);
    box-shadow: 
        0 0 8px rgba(0, 150, 255, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(135deg, rgba(150, 200, 255, 0.9), rgba(200, 220, 255, 0.7));
    box-shadow: 0 0 12px rgba(0, 200, 255, 0.6);
}

/* Frozen buttons with ice texture */
button, .button {
    background: 
        linear-gradient(135deg, rgba(30, 50, 90, 0.9), rgba(20, 40, 80, 0.8)),
        repeating-linear-gradient(45deg, transparent 0px, rgba(255, 255, 255, 0.02) 1px, transparent 2px);
    border: 2px solid rgba(100, 150, 200, 0.8);
    color: rgba(200, 220, 240, 1);
    border-radius: 10px;
    transition: all 0.3s ease;
    box-shadow: 
        0 0 10px rgba(0, 150, 255, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
    text-shadow: 0 0 4px rgba(0, 150, 255, 0.5);
}

button:hover, .button:hover {
    background: 
        linear-gradient(135deg, rgba(40, 60, 100, 0.9), rgba(30, 50, 90, 0.8)),
        repeating-linear-gradient(45deg, transparent 0px, rgba(255, 255, 255, 0.03) 1px, transparent 2px);
    box-shadow: 0 0 15px rgba(0, 200, 255, 0.5);
    transform: translateY(-2px);
}

/* Frozen input with ice crystals */
#send_textarea {
    background: rgba(20, 40, 80, 0.9);
    border: 2px solid rgba(100, 150, 200, 0.8);
    color: rgba(200, 220, 240, 1);
    border-radius: 10px;
    box-shadow: 
        inset 0 0 10px rgba(0, 150, 255, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
    text-shadow: 0 0 4px rgba(0, 150, 255, 0.3);
}

#send_textarea:focus {
    border-color: rgba(0, 200, 255, 1);
    box-shadow: 
        0 0 15px rgba(0, 200, 255, 0.4),
        inset 0 0 10px rgba(0, 150, 255, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

/* Ice-themed sidebar with frost */
.sidebar {
    background: rgba(10, 30, 60, 0.95);
    border-right: 3px solid rgba(100, 150, 200, 0.8);
    box-shadow: 
        3px 0 15px rgba(0, 150, 255, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

/* Frozen links */
a {
    color: rgba(150, 200, 255, 1);
    text-decoration: underline;
    text-decoration-color: rgba(100, 150, 200, 0.6);
    text-shadow: 0 0 4px rgba(0, 150, 255, 0.5);
    transition: all 0.3s ease;
}

a:hover {
    color: rgba(0, 200, 255, 1);
    text-decoration-color: rgba(0, 200, 255, 1);
    text-shadow: 0 0 8px rgba(0, 200, 255, 0.8);
}

/* Ice crystal headers */
h1, h2, h3, h4, h5, h6 {
    color: rgba(200, 220, 240, 1);
    text-shadow: 
        0 0 10px rgba(0, 150, 255, 0.6),
        0 0 20px rgba(0, 150, 255, 0.3);
    font-weight: 300;
}
