custom_css:
/* Sepia - Dark Vintage Theme */
body {
    color: rgba(100, 70, 50, 1);
    background-color: rgba(180, 150, 120, 0.95);
    background-attachment: fixed;
    overflow-x: hidden;
}

/* Dark vintage sepia background */
body {
    background: 
        radial-gradient(circle at 20% 80%, rgba(140, 110, 80, 0.4) 0%, transparent 60%),
        radial-gradient(circle at 80% 20%, rgba(120, 90, 60, 0.4) 0%, transparent 60%),
        linear-gradient(135deg, rgba(180, 150, 120, 0.95), rgba(160, 130, 100, 0.9));
    background-size: 100% 100%, 100% 100%, 100% 100%;
    position: relative;
}

/* Add vintage paper texture overlay */
body::before {
    content: '';
    position: fixed;
    inset: 0;
    background-image: 
        radial-gradient(circle at 1px 1px, rgba(100, 70, 50, 0.2) 1px, transparent 0),
        linear-gradient(45deg, transparent 48%, rgba(120, 90, 60, 0.1) 49%, rgba(120, 90, 60, 0.1) 51%, transparent 52%);
    background-size: 15px 15px, 30px 30px;
    opacity: 0.4;
    pointer-events: none;
    z-index: 1;
}

/* Dark vintage message bubbles */
.mes, .mesText, .swipe-message {
    background: linear-gradient(135deg, rgba(170, 140, 110, 0.9), rgba(165, 135, 105, 0.8));
    border: 3px solid rgba(140, 100, 70, 0.9);
    box-shadow: 
        0 6px 16px rgba(60, 40, 25, 0.4),
        inset 0 2px 4px rgba(200, 180, 160, 0.3);
    backdrop-filter: blur(6px);
    border-radius: 15px;
    transition: all 0.3s ease;
    position: relative;
    z-index: 2;
}

.mes:hover {
    background: linear-gradient(135deg, rgba(165, 135, 105, 0.95), rgba(160, 130, 100, 0.9));
    box-shadow: 
        0 8px 20px rgba(60, 40, 25, 0.5),
        inset 0 2px 4px rgba(200, 180, 160, 0.4);
    transform: translateY(-3px);
}

/* Dark vintage code blocks */
.chat_mes_block .mes_text p code {
    background: rgba(120, 90, 60, 0.6);
    color: rgba(200, 180, 160, 1);
    border-radius: 8px;
    padding: 4px 10px;
    border: 2px solid rgba(140, 100, 70, 0.7);
    font-family: 'Courier New', monospace;
    box-shadow: inset 0 2px 4px rgba(60, 40, 25, 0.3);
}

/* Dark vintage quote styling */
.chat_mes_block .mes_text blockquote {
    border-left: 5px solid rgba(140, 100, 70, 0.9);
    background: rgba(160, 130, 100, 0.4);
    padding-left: 20px;
    border-radius: 8px;
    font-style: italic;
    position: relative;
    box-shadow: inset 0 2px 4px rgba(60, 40, 25, 0.2);
}

.chat_mes_block .mes_text blockquote::before {
    content: '"'; 
    font-size: 2.5em;
    color: rgba(140, 100, 70, 0.8);
    position: absolute;
    left: 8px;
    top: -8px;
    font-family: serif;
    text-shadow: 2px 2px 4px rgba(60, 40, 25, 0.3);
}

/* Dark vintage input area */
#send_textarea {
    background: rgba(180, 150, 120, 0.9);
    border: 3px solid rgba(140, 100, 70, 0.9);
    color: rgba(100, 70, 50, 1);
    border-radius: 10px;
    box-shadow: inset 0 3px 6px rgba(60, 40, 25, 0.3);
    font-family: 'Times New Roman', serif;
    font-size: 1.1em;
}

#send_textarea:focus {
    border-color: rgba(120, 85, 65, 1);
    box-shadow: 
        0 0 10px rgba(120, 85, 65, 0.4),
        inset 0 3px 6px rgba(60, 40, 25, 0.3);
}

/* Dark vintage button styling */
button, .button {
    background: linear-gradient(135deg, rgba(170, 140, 110, 0.9), rgba(165, 135, 105, 0.8));
    border: 3px solid rgba(140, 100, 70, 0.9);
    color: rgba(100, 70, 50, 1);
    border-radius: 10px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 8px rgba(60, 40, 25, 0.3);
    font-family: 'Times New Roman', serif;
    font-weight: bold;
}

button:hover, .button:hover {
    background: linear-gradient(135deg, rgba(165, 135, 105, 0.95), rgba(160, 130, 100, 0.9));
    border-color: rgba(120, 85, 65, 1);
    box-shadow: 0 6px 12px rgba(60, 40, 25, 0.4);
    transform: translateY(-2px);
}

/* Dark vintage sidebar */
.sidebar {
    background: rgba(180, 150, 120, 0.95);
    border-right: 4px solid rgba(140, 100, 70, 0.9);
    box-shadow: 3px 0 12px rgba(60, 40, 25, 0.3);
}

/* Dark vintage selection highlight */
::selection {
    background: rgba(140, 100, 70, 0.6);
    color: rgba(200, 180, 160, 1);
}

/* Dark vintage scrollbar */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    background: rgba(160, 130, 100, 0.8);
    border-radius: 6px;
    border: 1px solid rgba(140, 100, 70, 0.5);
}

::-webkit-scrollbar-thumb {
    background: rgba(140, 100, 70, 0.9);
    border-radius: 6px;
    border: 2px solid rgba(120, 85, 65, 0.7);
    box-shadow: inset 0 2px 4px rgba(60, 40, 25, 0.3);
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(120, 85, 65, 0.9);
}

/* Dark vintage links */
a {
    color: rgba(120, 85, 65, 1);
    text-decoration: underline;
    text-decoration-color: rgba(140, 100, 70, 0.8);
    font-weight: bold;
}

a:hover {
    color: rgba(100, 70, 50, 1);
    text-decoration-color: rgba(120, 85, 65, 1);
}

/* Dark vintage headers */
h1, h2, h3, h4, h5, h6 {
    color: rgba(80, 55, 35, 1);
    font-family: 'Times New Roman', serif;
    text-shadow: 2px 2px 4px rgba(60, 40, 25, 0.5);
    font-weight: bold;
}

/* Dark vintage form elements */
input, select, textarea {
    background: rgba(180, 150, 120, 0.9);
    border: 3px solid rgba(140, 100, 70, 0.9);
    color: rgba(100, 70, 50, 1);
    border-radius: 8px;
    font-family: 'Times New Roman', serif;
    box-shadow: inset 0 2px 4px rgba(60, 40, 25, 0.3);
}

input:focus, select:focus, textarea:focus {
    border-color: rgba(120, 85, 65, 1);
    box-shadow: 
        0 0 8px rgba(120, 85, 65, 0.4),
        inset 0 2px 4px rgba(60, 40, 25, 0.3);
}

/* Add dark vintage paper aging effect */
.mes::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
        radial-gradient(circle at 20% 20%, rgba(120, 90, 60, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 80%, rgba(100, 70, 50, 0.1) 0%, transparent 50%);
    pointer-events: none;
    border-radius: 15px;
}

/* Dark vintage navigation */
.navbar, .header {
    background: rgba(160, 130, 100, 0.95);
    border-bottom: 3px solid rgba(140, 100, 70, 0.9);
    box-shadow: 0 3px 8px rgba(60, 40, 25, 0.3);
}

/* Dark vintage footer */
.footer {
    background: rgba(160, 130, 100, 0.95);
    border-top: 3px solid rgba(140, 100, 70, 0.9);
    box-shadow: 0 -3px 8px rgba(60, 40, 25, 0.3);
}
